import Select from "react-select";

export default function ServiceSelector(props) {
  return (
      <Select
        className="service-selector"
        options={props.serviceList}
        isMulti
        closeMenuOnSelect={false}
        onChange={(selected) => {
          selected = selected.map(el => {
            return el.value;
          })
          props.updateSelected(selected)
        }}
      />
  )
};
