import Select from "react-select";

const INTERNET = 1;
const TV = 2;
const PHONE_SUB = 4;
const DECODER = 8;
const INTERNET_TV = INTERNET + TV; // 3
const INTERNET_PHONE = INTERNET + PHONE_SUB; // 5

export default function ServiceSelector(props) {
  const handleServicesChange = (selectedServices) => {
    if(selectedServices.findIndex(service => service.value === TV) >= 0) //JESLI WYBRANO TELEWIZJE MOZNA WYBRAC DEKODER
      props.serviceList.find(service => service.value === DECODER).isDisabled = false
    else{ //JESLI NIE WYBRANO TELEWIZJI DEKODER NIE JEST DOSTEPNY DO WYBORU ORAZ JEST USUWANY Z LISTY JESLI W NIEJ BYL BO OZNACZA TO USUNIECIE TV Z LISTY
      props.serviceList.find(service => service.value === DECODER).isDisabled = true;
      if(selectedServices.findIndex(service => service.value === DECODER) >= 0)
        selectedServices.splice(selectedServices.findIndex(service => service.value === DECODER), 1)
    }
    //WYCIAGAMY SAME INDEKSY WYBRANYCH USLUG
    selectedServices = selectedServices.map(el => {
      return el.value;
    })

    //AKTUALIZUJEMY WYBRANE USLUGI
    props.updateSelectedServices(selectedServices)
  }

  return (
      <Select
        className="service-selector"
        options={props.serviceList}
        isMulti
        closeMenuOnSelect={false}
        onChange={(selectedServices) => handleServicesChange}
      />
  )
};
