import './App.css';
import Navbar from "./components/Navbar/Navbar";
import ServiceSelector from "./components/ServiceSelector/ServiceSelector";
import ServiceCalculator from "./components/ServiceCalculator/ServiceCalculator";
import {useState} from "react";

const INTERNET = 0;
const TV = 1;
const PHONE_SUB = 2;
const DECODER = 3;
const INTERNET_TV = 4;
const INTERNET_PHONE = 5;

const pricesMatrix = [
  { // INTERNET
    '2023': 39,
    '2024': 49,
    '2025': 59
  },
  { // TV
    '2023': 49,
    '2024': 49,
    '2025': 59
  },
  { // PHONE_SUB
    '2023': 29,
    '2024': 29,
    '2025': 29
  },
  { // DECODER
    '2023': 29,
    '2024': 29,
    '2025': 29
  },
  { // INTERNET_TV
    '2023': 79,
    '2024': 89,
    '2025': 99
  },
  { // INTERNET_PHONE
    '2023': 64,
    '2024': 64,
    '2025': 64
  }
]

const serviceList = [
  {value: INTERNET, label:'Internet'},
  {value: TV, label: 'Telewizja',},
  {value: PHONE_SUB, label: 'Abonament telefoniczny'},
  {value: DECODER, label: 'Dekoder 4K', isDisabled: true}
];

const yearOptions = [
  {value: 2023, label: '2023'},
  {value: 2024, label: '2024'},
  {value: 2025, label: '2025'}
];

function App() {
  const [selectedServices, setSelectedServices] = useState();
  const [selectedYear, setSelectedYear] = useState();

  const updateSelectedServices = (services) => {
    window.services = services;
    console.log(services);
    setSelectedServices(services);
  }

  const updateSelectedYear = (year) => {
    window.year = year;
    console.log(year);
    setSelectedYear(year);
  }

  window.pricesMatrix = pricesMatrix;
  window.serviceList = serviceList;
  console.log(pricesMatrix);
  return (
    <div className="App">
      <Navbar />

      <ServiceSelector serviceList={serviceList} updateSelectedServices={updateSelectedServices}/>

      <ServiceCalculator yearOptions={yearOptions} selectedServices={selectedServices}/>

      <footer className="footer">
        Bartosz WiecheÄ‡
      </footer>
    </div>
  );
}

export default App;
