import Select from "react-select";
import {useState} from "react";

const INTERNET = 1;
const TV = 2;
const PHONE_SUB = 4;
const DECODER = 8;
const INTERNET_TV = INTERNET + TV; // 3
const INTERNET_PHONE = INTERNET + PHONE_SUB; // 5

export default function ServiceCalculator(props) {
  const [selectedYear, setSelectedYear] = useState();

  const findBestDeal = () => {
    let totalPrice = 0;
    let selectedIndexes = [];

    if(!selectedYear) return totalPrice;

    props.selectedServices?.forEach(service => {
      console.log(props.pricesMatrix[service][selectedYear]);
      let servicesTemp = [...props.selectedServices].splice(props.selectedServices.findIndex(serv => serv === service), 1)
      console.log(`servicesTemp: ${servicesTemp.toString()}`);
      let selectedIndexesTemp = [];
      let max = 0
      console.log(`service: ${service}`);
      servicesTemp.forEach(serviceTemp => {
        console.log(`serviceTemp: ${serviceTemp}`);
        totalPrice = 0;
        let servicesTempToSum = [...servicesTemp].splice(servicesTemp.findIndex(serv => serv === serviceTemp), 1);
        console.log(`servicesTempToSum: ${servicesTempToSum.toString()}`);
        if(props.pricesMatrix[service + serviceTemp][selectedYear]){
          totalPrice += props.pricesMatrix[service + serviceTemp][selectedYear]
          selectedIndexesTemp.push(service + serviceTemp);
        }
        else{
          totalPrice += props.pricesMatrix[service][selectedYear] + props.pricesMatrix[serviceTemp][selectedYear];
          selectedIndexesTemp.push(service);
          selectedIndexesTemp.push(serviceTemp);

        }

        servicesTempToSum.forEach(serviceToSum => {
          console.log(`serviceToSum: ${serviceToSum}`);
          serviceToSum === DECODER && (service + serviceTemp) === INTERNET_TV?
            totalPrice += 0
              :
            totalPrice += props.pricesMatrix[service][serviceToSum];
            selectedIndexesTemp.push(serviceToSum);
        })

        max = max < totalPrice ? totalPrice : max;
        selectedIndexes = max < totalPrice ? selectedIndexesTemp : selectedIndexes;
      })
    })
    console.log(`total price: ${totalPrice}`);
    console.log(`selectedIndexes: ${selectedIndexes.toString()}`);
    console.log(`props.selectedServices: ${props.selectedServices.toString()}`);
    return totalPrice;
  }

  findBestDeal();

  return (
    <div className="service-calculator">

      <Select
        options={props.yearOptions}
        onChange={(selected) => {
          console.log(selected);
          window.selectedYear = selected;
          setSelectedYear(selected.value)
        }}
      />

      <p>Suma: {findBestDeal()}</p>
    </div>
  )
};
