import Select from "react-select";

const INTERNET = 0;
const TV = 1;
const PHONE_SUB = 2;
const DECODER = 3;
const INTERNET_TV = 4;
const INTERNET_PHONE = 5;

export default function ServiceSelector(props) {
  return (
      <Select
        className="service-selector"
        options={props.serviceList}
        isMulti
        closeMenuOnSelect={false}
        onChange={(selectedServices) => {
          console.log(selectedServices);
          window.selectedServices = selectedServices;
          if(selectedServices.findIndex(service => service.value === TV) > 0)
            selectedServices.find(service => service.value === DECODER).disabled = false;
          selectedServices = selectedServices.map(el => {
            return el.value;
          })
          props.updateSelectedServices(selectedServices)
        }}
      />
  )
};
