import './App.css';
import Navbar from "./components/Navbar/Navbar";
import ServiceSelector from "./components/ServiceSelector/ServiceSelector";
import ServiceCalculator from "./components/ServiceCalculator/ServiceCalculator";
import {useState} from "react";

const INTERNET = 1;
const TV = 2;
const PHONE_SUB = 4;
const DECODER = 8;
const INTERNET_TV = INTERNET + TV; // 3
const INTERNET_PHONE = INTERNET + PHONE_SUB; // 5

const serviceNames = {
  [INTERNET]: 'Internet',
  [TV]: 'Telewizja',
  [PHONE_SUB]: 'Abonament telefoniczny',
  [DECODER]: 'Dekoder 4K',
  [INTERNET_TV]: 'Internet + telewizja',
  [INTERNET_PHONE]: 'Internet + Abonament telefoniczny'
};

const pricesMatrix = {
  [INTERNET]: { // INTERNET
    '2023': 39,
    '2024': 49,
    '2025': 59
  },
  [TV]: { // TV
    '2023': 49,
    '2024': 49,
    '2025': 59
  },
  [PHONE_SUB]: { // PHONE_SUB
    '2023': 29,
    '2024': 29,
    '2025': 29
  },
  [DECODER]: { // DECODER
    '2023': 29,
    '2024': 29,
    '2025': 29
  },
  [INTERNET_TV]: { // INTERNET_TV
    '2023': 79,
    '2024': 89,
    '2025': 99
  },
  [INTERNET_PHONE]: { // INTERNET_PHONE
    '2023': 64,
    '2024': 64,
    '2025': 64
  }
}

const serviceList = [
  {value: INTERNET, label:'Internet'},
  {value: TV, label: 'Telewizja',},
  {value: PHONE_SUB, label: 'Abonament telefoniczny'},
  {value: DECODER, label: 'Dekoder 4K', isDisabled: true}
];

const yearOptions = [
  {value: 2023, label: '2023'},
  {value: 2024, label: '2024'},
  {value: 2025, label: '2025'}
];

function App() {
  const [selectedServices, setSelectedServices] = useState();

  const updateSelectedServices = (services) => {
    window.services = services;
    console.log(services);
    setSelectedServices(services);
  }

  window.pricesMatrix = pricesMatrix;
  window.serviceList = serviceList;
  console.log(pricesMatrix);
  return (
    <div className="App">
      <Navbar />

      <ServiceSelector
        serviceList={serviceList}
        updateSelectedServices={updateSelectedServices}
      />

      <ServiceCalculator
        yearOptions={yearOptions}
        pricesMatrix={pricesMatrix}
        selectedServices={selectedServices}
        serviceNames={serviceNames}
      />

      <footer className="footer">
        Bartosz WiecheÄ‡
      </footer>
    </div>
  );
}

export default App;
